var slideIndex=1;function plusSlides(e){showSlides(slideIndex+=e)}function currentSlide(e){showSlides(slideIndex=e)}function showSlides(e){var s,n=document.getElementsByClassName("mySlides"),l=document.getElementsByClassName("dot");for(e>n.length&&(slideIndex=1),e<1&&(slideIndex=n.length),s=0;s<n.length;s++)n[s].style.display="none";for(s=0;s<l.length;s++)l[s].className=l[s].className.replace(" active","");n[slideIndex-1].style.display="block",l[slideIndex-1].className+=" active"}function changeImg(e){document.getElementsByClassName("active").classList.toggle("active"),e.classList.toggle("active")}showSlides(slideIndex);var graphIndex=1;function nextGraph(e){showGraph(graphIndex==e)}function currentGraph(e){showGraph(graphIndex=e)}function showGraph(e){var s,n=document.getElementsByClassName("myGraphs");for(e>n.length&&(graphIndex=1),e<1&&(graphIndex=n.length),s=0;s<n.length;s++)n[s].style.display="none";n[graphIndex-1].style.display="block"}showGraph(graphIndex);